mapper.insert:
	insert into 
		customer
        (
            custno
            , custid
            , custpwd
            , custname
            , custgender
            , custemail
            , custage
            , custphone
            , custaddress
            , skintype
        )
	values (
		SEQ_CUSTOMER_CUSTNO.nextval
		, #{custid}
		, #{custpwd}
		, #{custname}
		, #{custgender}
		, #{custemail}
		, #{custage}
		, #{custphone}
		, #{custaddress}
		, #{skintype}
	)

mapper.update:
update
	customer
set
	custpwd = #{custpwd}
	, custname = #{custname}
	, custgender = #{custgender}
	, custemail = #{custemail}
	, custage = #{custage}
	, custphone = #{custphone}
	, custaddress = #{custaddress}
where
	custid = #{custid}
