mapper.insert:
	insert into 
		customer
        (
            custno
            , custid
            , custpwd
            , custname
            , custgender
            , custemail
            , custage
            , custphone
            , custaddress
        )
	values (
		SEQ_CUSTOMER_CUSTNO.nextval
		, #{custid}
		, #{custpwd}
		, #{custname}
		, #{custgender}
		, #{custemail}
		, #{custage}
		, #{custphone}
		, #{custaddress}
	)
